import React from 'react';

class PopupWithForm extends React.Component{
    constructor({name, title, value, isOpen, onClose, handleSubmit, children}) {
        super({name, title, value, isOpen, onClose, handleSubmit, children});
        this.state = {
            value: ''
        };
        this.handleSubmit = this.handleSubmit.bind(this)

    }

    render() {
        return(
            <section className={`popup section__popup popup_type_${this.name} ${this.isOpen ? 'popup_opened' : ''}`}>
            <div className="popup__container">
                <form
                    name={this.name || ''}
                    className={`popup__form popup-form_type_${this.name}`}
                    noValidate
                    onSubmit={this.handleSubmit}
                >
                    <h2 className="popup__heading section__subtitle">
                        {this.title}
                    </h2>
                    <fieldset className="popup__fields">
                        {this.children}
                    </fieldset>
                    <input
                        name="save-button"
                        type="submit"
                        value={this.state.value || ""}
                        className="popup__save-button"
                    />
                    <button
                        name="close-button"
                        type="button"
                        className="popup__close-button buttons"
                        onClick={this.onClose}
                    ></button>
                </form>
            </div>
        </section>
        )
    }

}

