import { useState, useContext, useEffect } from "react";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import PopupWithForm from "./PopupWithForm";

function EditProfilePopup({ isOpen, onClose, onUpdateUser, valueInput }) {
    const [name, setName] = useState("");
    const [description, setDescription] = useState("");
    const currentUser = useContext(CurrentUserContext);
    const [nameError, setNameError] = useState("");
    const [descriptionError, setDescriptionError] = useState("");

    useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about);
    }, [currentUser]);

    function handleSubmit(event) {
        event.preventDefault();

        onUpdateUser({
            name,
            about: description,
        });
    }

    function handleChangeName(event) {
        setName(event.target.value);
        if (event.target.checkValidity()) {
            setNameError('');
        } else {
            setNameError(event.target.validationMessage);
        }
    }

    function handleChangeDescription(event) {
        setDescription(event.target.value);

    }

    function handleCheckValidityName(event) {
        if (event.target.checkValidity()) {
            setNameError('');
            setDescriptionError('');
        } else {
            setNameError(event.target.validationMessage);
            setDescriptionError(event.target.validationMessage);
        }
    }

    return (
        <PopupWithForm
            name="popup-edit-profile"
            title="Редактировать профиль"
            value={valueInput}
            isOpen={isOpen}
            onClose={onClose}
            handleSubmit={handleSubmit}
        >
            <input
                name="name"
                type="text"
                value={name || ""}
                onChange={event => handleChangeName(event)}
                className="popup__input popup__input_name"
                id="user-name"
                minLength="2"
                maxLength="40"
                required
                autoComplete="off"
            />
            <span id="user-name-error" className="error">
                {nameError}
            </span>
            <input
                name="job"
                type="text"
                value={description || ""}
                onChange={event => handleChangeDescription(event)}
                className="popup__input popup__input_job"
                id="about"
                minLength="2"
                maxLength="200"
                required
                autoComplete="off"
            />
            <span id="about-error" className="error">{descriptionError}</span>
        </PopupWithForm>
    );
}

export default EditProfilePopup;
