import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import InfoToolTip from "./InfoTooltip";

const Register = (props) => {
    const [ data, setData ] = useState({
        email: '',
        password: '',
    })
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [popupMessage, setPopupMessage] = useState("");
    const [isRegisterPopup, setIsRegisterPopup] = useState(false);
    const [submitStatus, setSubmitStatus] = useState(false);
    const history = useHistory();

    useEffect(() => {
        if (props.loggedIn) history.push("/");
    }, [props.loggedIn, history]);

    function onChange(e) {
        setData({
            [data]: e.target.value
        })
    }

    // function onChangeEmail(e) {
    //     setEmail(e.target.value);
    // }

    // function onChangePassword(e) {
    //     setPassword(e.target.value);
    // }

    function onClose() {
        setIsRegisterPopup(false);
        if (submitStatus) {
            history.push("/sign-in");
        }
    }

    function onSubmit(e) {
        e.preventDefault();
        props
            .onRegister(email, password)
            .then(() => {
                setSubmitStatus(true);
                setPopupMessage("Вы успешно зарегистрировались!");
            })
            .catch(() => {
                setPopupMessage("Что-то пошло не так! Попробуйте ещё раз.");
            })
            .finally(() => {
                setIsRegisterPopup(true);
            });
    }

    return (
        <div className="register">
            <h2 className="register__heading">Регистрация</h2>
            <form
                onSubmit={onSubmit}
                className="register__form"
                autoComplete="off"
            >
                <input
                    id="email"
                    name="email"
                    type="email"
                    value={data.email}
                    onChange={onChange}
                    className="register__input"
                    placeholder="Email"
                />
                <input
                    id="password"
                    name="password"
                    type="password"
                    value={data.password}
                    onChange={onChange}
                    className="register__input"
                    placeholder="Пароль"
                />
                <button type="submit" className="register__submit-button">
                    Зарегистрироваться
                </button>
            </form>
            <p className="register__title">
                Уже регистрировались?
                <Link className="register__link" to="/sign-in">
                    Войти
                </Link>
            </p>
            <InfoToolTip
                isOpen={isRegisterPopup}
                onClose={onClose}
                name="register"
                status={submitStatus}
            >
                {popupMessage}
            </InfoToolTip>
        </div>
    );
};

export default Register;
