import React, { useState, useEffect } from "react";
import Card from './Card';
import api from "../utils/api.js";
import profilePhoto from "../images/jacques-photo.png";
import photoEdit from "../images/edit-avatar.svg";

function Main(props) {
    const [userName, setUserName] = useState("Жак-Ив Кусто");
    const [userDescription, setUserDescription] = useState(
        "Исследователь океана"
    );
    const [userAvatar, setUserAvatar] = useState(profilePhoto);
    const [cards, setCards] = useState([]);

    // Получение карточек с сервера
    useEffect(() => {
        api.getUserInfo()
            .then((data) => {
                setUserName(data.name);
                setUserDescription(data.about);
                setUserAvatar(data.avatar);
            })
            .catch((err) => {
                console.log(err);
            });
    });

    // Получение карточек с сервера
    useEffect(() => {
        api.getInitialCards()
            .then((cards) => {
                setCards(cards);
            })
            .catch((err) => {
                console.log(err);
            });
    });

    const cardList = cards.map((card) => (
        <li className="elements__item">
            <figure className="element">
                <img src={card.link} alt={card.name} className="element__image" />
                <div className="element__body">
                    <p className="element__figcaption section__subtitle">{card.name}</p>
                    <div className="element__like-group">
                        <button
                            className="element__like-button buttons"
                            type="button"
                        ></button>
                        <div className="element__like-count">{card.likes.length}</div>
                    </div>
                </div>
                <button
                    className="element__delete-button buttons"
                    type="button"
                ></button>
            </figure>
        </li>
    ));

    return (
        <main className="content section section__content">
            <section className="profile section__profile">
                <div className="profile__wrap">
                    <img
                        src={userAvatar}
                        alt={userName}
                        className="profile__avatar"
                    />

                    <img
                        src={photoEdit}
                        alt="Смена аватара"
                        className="profile__avatar-edit"
                        onClick={props.onEditAvatar}
                    />
                </div>

                <div className="profile__info">
                    <h1 className="profile__author section__title">
                        {userName}
                    </h1>
                    <button
                        type="button"
                        className="profile__edit-button buttons"
                        onClick={props.onEditProfile}
                    ></button>
                    <p className="profile__author-status section__subtitle">
                        {userDescription}
                    </p>
                </div>
                <button
                    type="button"
                    className="profile__add-button buttons"
                    onClick={props.onAddPlace}
                ></button>
            </section>

            <section className="elements">
                <ul className="elements__list">{cardList}</ul>
            </section>
        </main>
    );
}

export default Main;
