import { popupAvatar } from "../utils/utils";

function PopupWithForm({
    name,
    title,
    value,
    isOpen,
    onClose,
    handleSubmit,
    children,
}) {
    // console.log(isOpen)
    return (
        <section
            className={`popup section__popup popup_type_${name} ${
                isOpen ? "popup_opened" : ""
            }`}
        >
            <div className="popup__container">
                <form
                    name={name}
                    className={`popup__form popup-form_type_${name}`}
                    noValidate
                    onSubmit={handleSubmit}
                >
                    <h2 className="popup__heading section__subtitle">
                        {title}
                    </h2>
                    <fieldset className="popup__fields">{children}</fieldset>
                    <input
                        name="save-button"
                        type="submit"
                        value={value}
                        className={`popup__save-button ${handleSubmit ? 'popup__save-button_invalid' : ''}`}
                        disabled={handleSubmit === false ? true : false}
                    />
                    <button
                        name="close-button"
                        type="button"
                        className="popup__close-button buttons"
                        onClick={onClose}
                    ></button>
                </form>
            </div>
        </section>
    );
}

export default PopupWithForm;
