import { useState, useContext, useEffect } from "react";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import PopupWithForm from "./PopupWithForm";

function EditProfilePopup({ isOpen, onClose, onUpdateUser, valueInput }) {
    const [name, setName] = useState('');
    const [job, setJob] = useState('');
    const [description, setDescription] = useState('');
    const currentUser = useContext(CurrentUserContext);
    const [nameDirty, setNameDirty] = useState(false);
    const [jobDirty, setJobDirty] = useState(false);
    const [nameError, setNameError] = useState('Поле не может быть пустым');
    const [jobError, setJobError] = useState('Поле не может быть пустым')

    const blurHandler = (event) => {
        switch (event.target.name) {
            case 'name':
                setNameDirty(true)
                break
            case 'job':
                setJobDirty(true)
                break
        }
    }


    useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about);
    }, [currentUser]);

    function handleSubmit(event) {
        event.preventDefault();

        onUpdateUser({
            name,
            about: description,
        });
    }

    return (
        <PopupWithForm
            name="popup-edit-profile"
            title="Редактировать профиль"
            value={valueInput}
            isOpen={isOpen}
            onClose={onClose}
            handleSubmit={handleSubmit}
        >
            <input
                name="name"
                type="text"
                value={name || ""}
                onChange={(evt) => setName(evt.target.value)}
                className="popup__input popup__input_name"
                onBlur={e => blurHandler(e)}
                id="user-name"
                minLength="2"
                maxLength="40"
                required
                autoComplete="off"
            />
            <span id="user-name-error" className="error">{`${(nameDirty && nameError) ? nameError : ''}`}</span>
            <input
                name="job"
                type="text"
                value={description || ""}
                onChange={(evt) => setDescription(evt.target.value)}
                className="popup__input popup__input_job"
                id="about"
                minLength="2"
                maxLength="200"
                required
                autoComplete="off"
                onBlur={e => blurHandler(e)}
            />
            <span id="about-error" className="error">{`${(jobDirty && jobError) ? jobError : ''}`}</span>
        </PopupWithForm>
    );
}

export default EditProfilePopup;
