import { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import InfoToolTip from "./InfoTooltip";

function Login(props) {
    const [data, setData] = useState({
        currentEmail: "",
        currentPassword: "",
    });
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [popupMessage, setPopupMessage] = useState("");
    const [isLoginPopup, setIsLoginPopup] = useState(false);
    const [submitStatus, setSubmitStatus] = useState(false);
    const history = useHistory();

    useEffect(() => {
        if (props.loggedIn) history.push("/");
    }, [props.loggedIn, history]);

    function onChange(e) {
        const { name, value } = e.target;
        setData({
            ...data,
            [name]: value
        });
    }

    // function onChangeEmail(e) {
    //     setEmail(e.target.value);
    // }

    // function onChangePassword(e) {
    //     setPassword(e.target.value);
    // }

    function onClose() {
        setIsLoginPopup(false);
        if (submitStatus) {
            props.checkToken();
        }
    }

    function onSubmit(e) {
        e.preventDefault();
        props
            .onLogin(data.email, data.password)
            .then(() => {
                setSubmitStatus(true);
                setPopupMessage("Вы успешно вошли!");
            })
            .catch((e) => {
                setPopupMessage("Что-то пошло не так! Попробуйте ещё раз.");
            })
            .finally(() => {
                setIsLoginPopup(true);
            });
    }

    return (
        <div className="register">
            <h2 className="register__heading">Вход</h2>
            <form className="register__form" onSubmit={onSubmit}>
                <input
                    type="email"
                    name="email"
                    className="register__input"
                    placeholder="Email"
                    value={data.email}
                    onChange={onChange}
                />
                <input
                    type="current-password"
                    name="password"
                    className="register__input"
                    placeholder="Пароль"
                    value={data.password}
                    onChange={onChange}
                />
                <button className="register__submit-button">Войти</button>
            </form>
            <InfoToolTip
                isOpen={isLoginPopup}
                onClose={onClose}
                name="login"
                status={submitStatus}
            >
                {popupMessage}
            </InfoToolTip>
        </div>
    );
}

export default Login;
